package javaHelloWorld;

public class conditions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = 20 ;
		if (number < 20) {
			System.out.println("The number is lower than 20");
		}
		else if (number >20){ 
			System.out.println("The number is higher than 20");	
		}
		else {
			System.out.println("The number is equal to 20");	
		}
		
		
	}

}
